import React, {useContext} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import PhotoLibraryIcon from '@material-ui/icons/PhotoLibrary';
import InfoIcon from '@material-ui/icons/Info';
import PermContactCalendarIcon from '@material-ui/icons/PermContactCalendar';
import FacebookIcon from '@material-ui/icons/Facebook';
import EmailIcon from '@material-ui/icons/Email';
import {MenuDrawerContext} from './Navbar.js';

const useStyles = makeStyles({
    drawer:{
        backgroundColor:"transparent"
    },
    list:{
        backgroundColor:'rgba(0,0,0, .6)',
        width:'250px',
        fontSize:'25px',
        color:'white',
        height:'100%'
    },
    divider:{
        margin:'20px'
    },
    icons:{
        color:'white'
    }
})

export default function MenuDrawer() {
    const drawer = useContext(MenuDrawerContext);
    const classes = useStyles();

    return (
        <Drawer 
            className={classes.drawer}
            anchor="right" 
            open={drawer.drawer} 
            onClose={()=>drawer.setDrawer(false)}>
            <List className={classes.list}>
                <ListItem>
                    <ListItemIcon>
                        <PhotoLibraryIcon fontSize="large" className={classes.icons}/>
                    </ListItemIcon>
                    Fish/Photos
                </ListItem>
                <ListItem>
                    <ListItemIcon>
                        <InfoIcon fontSize="large" className={classes.icons}/>
                    </ListItemIcon>
                    About
                </ListItem>
                <ListItem>
                    <ListItemIcon>
                        <PermContactCalendarIcon fontSize="large" className={classes.icons}/>
                    </ListItemIcon>
                    Contact
                </ListItem>
                <Divider className={classes.divider} />
                <ListItem>
                    <ListItemIcon>
                        <FacebookIcon fontSize="large" className={classes.icons}/>
                    </ListItemIcon>
                    Facebook
                </ListItem>
                <ListItem>
                    <ListItemIcon>
                        <EmailIcon fontSize="large" className={classes.icons}/>
                    </ListItemIcon>
                    Email
                </ListItem>
            </List>
        </Drawer>
    )
}
